import keyworddef leiafloat(msgem):    while True:        try:            m = float(input(msgem))        except (ValueError, TypeError):            print('\033[31mErro : Por Favor digite um valor float valido\033[m ')            continue        except (KeyboardInterrupt):            print('\n\033[31mUsuario preferiu não digitar um valor numerico.\033[m')            return 0        else:            return mdef leiaint(msg):    while True:        try:            n = int(input(msg))        except (ValueError, TypeError):            print('\033[31mErro: Por Favor, digite um numero inteiro valido.\033[m')            continue        except (KeyboardInterrupt):            print('\n\033[31mUsuario preferiu não digitar um valor numerico.\033[m')            return 0        else:            return ndef linha(tam=42):    return '-' * tamdef cabecalho(txt):    print(linha())    print(txt.center(42))    print(linha())def menu(lista):    cabecalho('MENU')    c = 1    for i in lista:        print(f'\033[33m{c}\033[m - \033[40m{i}\033[m')        c += 1    opc = leiaint('\033[40mSua opção :\033[m')    return opcdef arqExiste(nome):    try:        a = open(nome, 'rt')        a.close()    except FileNotFoundError:        return False    else:        return Truedef criararquivo(nome):    try:        a = open(nome, 'wt+')        a.close()    except:        print('Houve um erro ao tentar criar o arquivo !')    else:        print(f'Arquivo {nome} criado com sucesso !')def lerarquivo(nome):    try:        a = open(nome, 'rt')    except:        print('\033[31m ERRO !!! Não é possivel ler o arquivo !\033[m')    else:        cabecalho('PESSOAS CADASTRADAS')        for linha in a:            dado = linha.split(';')            dado[1] = dado[1].replace('\n', '')            print(f'{dado[0]:<30}{dado[1]:>3} anos')    finally:        a.close()def cadastrar(arq, nome='desconhecido', idade=0):    try:        a = open(arq, 'at')    except:        print('Houve um ERRO ao tentar abrir o arquivo')    else:        try:            a.write(f'{nome};{idade}\n')        except:            print('Ocorreu um erro ao tentar inserir novos dados !')        else:            print(f'Novo registro de {nome} adicionado !')